public class TMFerlizerUseInfoActivity extends TMBaseActivity {
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_tmfui);

        setContainer((ViewGroup) findViewById(R.id.frameLayout_tmfui));
        SharedPreferences sp = this.getSharedPreferences("TMSharedPreferences", Context.MODE_PRIVATE);
        final String userName=sp.getString("UserName","tmnull");
        RenderJson j =new RenderJson();
        j.src= TMConstant.WebServer+TMConstant.fertilizerUseInfo+"?UserAID="+userName;
        String appData= WXJsonUtils.fromObjectToJSONString(j);
        renderPage(WXFileUtils.loadAsset("tmWeb.js", TMFerlizerUseInfoActivity.this),null,appData);
    }
    class RenderJson{
        String src="";
    }
}
