- (instancetype)initWithRef:(NSString *)ref type:(NSString *)type styles:(NSDictionary *)styles attributes:(NSDictionary *)attributes events:(NSArray *)events weexInstance:(WXSDKInstance *)weexInstance
{
    if (self = [super initWithRef:ref type:type styles:styles attributes:attributes events:events weexInstance:weexInstance]) {
        
        _createFinished = NO;
        _renderFinished = NO;
        
        NSMutableString * newURL = [attributes[@"src"] mutableCopy];
        WX_REWRITE_URL(attributes[@"src"], WXResourceTypeLink, self.weexInstance, &newURL)
        
        _sourceURL = [NSURL URLWithString:newURL];
        _visible =  [WXConvert WXVisibility:styles[@"visibility"]];
        
        [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(observeInstanceState:) name:WX_INSTANCE_NOTIFICATION_UPDATE_STATE object:nil];
    }
    
    return self;
}
