<template>
    <div class="drawer-wrapper" ref="wrapper" @click="hideDrawer" v-if="show">
        <scroller class="drawer" ref="drawer">
            <div class="drawer-header"></div>
            <text>菜单1</text>
            <text>菜单1</text>
            <text>菜单1</text>
            <text>菜单1</text>
        </scroller>
    </div>
</template>
<style>
    .drawer-wrapper{
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        background-color: rgba(0, 0, 0, 0.3);
        opacity: 0;
    }
    .drawer{
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        width: 200px;
        background-color: #eeeeee;
        transform: translate(-200px, 0px);
    }
    .drawer-header{
        height: 100px;
        background-image: linear-gradient(to top left,#28520c,#66ff00);
    }

</style>
<script>
    const animation = weex.requireModule('animation');
    const modal = weex.requireModule('modal');
    export default{
        data(){
            return{
                show: false,
            }
        },
        methods: {
            hideDrawer(){
                const wrapper = this.$refs.wrapper;
                const drawer = this.$refs.drawer;
                animation.transition(drawer,
                    {
                        styles: {
                            color: '#FF0000',
                            transform: 'translate(-200px, 0px)',
                            transformOrigin: 'center center'
                        },
                        duration: 300, //ms
                        timingFunction: 'ease',
                        delay: 0 //ms
                    },
                );
                animation.transition(wrapper,
                    {
                        styles: {
                            opacity: 0,
                        },
                        duration: 300, //ms
                        timingFunction: 'ease',
                        delay: 300 //ms
                    },
                    ()=> {
                        this.show = false;
                    }
                );
            },
            showDrawer(){
                const wrapper = this.$refs.wrapper;
                const drawer = this.$refs.drawer;
                this.show = true;
                animation.transition(wrapper,
                    {
                        styles: {
                            opacity: 1,
                        },
                        duration: 300, //ms
                        timingFunction: 'ease',
                        delay: 0 //ms
                    }
                );
                animation.transition(drawer,
                    {
                        styles: {
                            color: '#FF0000',
                            transform: 'translate(0px, 0px)',
                            transformOrigin: 'center center'
                        },
                        duration: 300, //ms
                        timingFunction: 'ease',
                        delay: 300 //ms
                    },
                );
            },
        },
        mounted(){
            console.log(this.$refs, this.$refs.wrapper); // TODO：为什么无法获取呢？
            this.eventBus.$on('show-drawer', this.showDrawer);
        }
    }

</script>

