<template>
      <div style="background-color:{{active? themeColor:'#f0f0f0'}}" repeat="{{timeList}}">
          <div data-index="{{index}}" 
                  onclick="tabClick" >
                <text>{{active}}</text>
          </div>
      </div>
</template>

<script>

    module.exports = {
        data: {
            themeColor:'#ff6699',
            data: {
                "timeList" : [{
                    "active": true
                }, {
                    "active": true
                }, {
                    "active": true
                }]
            }
        },
        methods: {
            ready: function () {
                //init data-bind
                for(var i = 0; i < this.data.timeList.length;i++){
                    this.data.timeList[i].active = false;
                    this.data.timeList[i].index = i;
                }

                this.timeList = this.data.timeList;
            },

            tabClick:function(e){
                var target = e.target;
                var index = target.attr.dataIndex;

                var currentItem = this.timeList[index];
                this.timeList[index].active = true;
            }
        }
    };
</script>
